import React from 'react';
import './styles.css';

import Article from './components/Article';

const App = () => {
    const newsData = [
        { title: "Tiêu đề tin tức 1", image: "img/anh1.png" },
        { title: "Tiêu đề tin tức 2", image: "img/anh2.png" },
        
    ];
    return (
        <div>
            <header>
                <h1>News Blog</h1>
            </header>
            <main>
                {newsData.map((news, index) => (
                    <Article key={index} title={news.title} image={news.image} />
                ))}
            </main>
            <footer>
                <p>&copy; 2024 News Blog</p>
            </footer>
        </div>
    );
}

export default App;
